mapper.select:
select * from ( select d.document_seq document_seq, write_date, name, emer_yn, title, d.status status, a.emp_no last_appr, done_date, row_number() over (order by write_date desc) as rn from documents d join document_list dl on d.docu_code = dl.docu_code join approvals_line a on d.document_seq = a.document_seq where a.approval_order = 3 and d.status != '임시 저장' and d.emp_no = #{empNo}
and d.docu_code = #{docuCode}
) where rn between (#{cPage} * #{recordCountPerPage} - (#{recordCountPerPage} - 1)) AND (#{cPage} * #{recordCountPerPage})

select * from ( select document_seq, write_date, name, emer_yn, title, row_number() over (order by write_date desc) as rn from documents d join document_list dl on d.docu_code = dl.docu_code where status = '임시 저장' and emp_no = #{empNo}
and d.docu_code = #{docuCode}
) where rn between (#{cPage} * #{recordCountPerPage} - (#{recordCountPerPage} - 1)) AND (#{cPage} * #{recordCountPerPage})

select * from ( select d.document_seq document_seq, d.emp_no emp_no, write_date, name, emer_yn, title, d.status status, done_date, row_number() over (order by write_date desc) as rn from documents d join document_list dl on d.docu_code = dl.docu_code join approvals_line a on d.document_seq = a.document_seq where a.emp_no = #{empNo} and a.status = '결재 완료' and d.status = '완료'
and d.docu_code = #{docuCode}
) where rn between (#{cPage} * #{recordCountPerPage} - (#{recordCountPerPage} - 1)) AND (#{cPage} * #{recordCountPerPage})

select * from ( select d.document_seq document_seq, d.emp_no emp_no, write_date, name, emer_yn, title, done_date, row_number() over (order by write_date desc) as rn from documents d join document_list dl on d.docu_code = dl.docu_code join approvals_line a on d.document_seq = a.document_seq where a.emp_no = #{empNo} and a.status = '결재 완료' and d.status = '반려'
and d.docu_code = #{docuCode}
) where rn between (#{cPage} * #{recordCountPerPage} - (#{recordCountPerPage} - 1)) AND (#{cPage} * #{recordCountPerPage})

select * from ( select d.document_seq document_seq, d.emp_no emp_no, write_date, name, emer_yn, title, d.status status, done_date, row_number() over (order by emer_yn desc, write_date desc) as rn from documents d join document_list dl on d.docu_code = dl.docu_code join reference_line r on d.document_seq = r.document_seq where r.emp_no = #{empNo}
and d.docu_code = #{docuCode}
) where rn between (#{cPage} * #{recordCountPerPage} - (#{recordCountPerPage} - 1)) AND (#{cPage} * #{recordCountPerPage})

mapper.update:
update leave_requests set leave_type = #{leave_type}, start_date = #{start_date}, end_date = #{end_date},
start_day_checked = #{start_day_checked}, start_day_am_checked = #{start_day_am_checked},
start_day_pm_checked = #{start_day_pm_checked}, end_day_checked = #{end_day_checked},
end_day_am_checked = #{end_day_am_checked}, end_day_pm_checked = #{end_day_pm_checked},
request_leave_days = #{request_leave_days}, reason = #{reason}
where document_seq = #{document_seq}

update approvals_line set status = #{status}

	, approved_date = sysdate 

where document_seq = #{docuSeq} and approval_order = #{order}

mapper.select:
select count(*) from documents d join document_list dl on d.docu_code = dl.docu_code join approvals_line a on d.document_seq = a.document_seq where and upper(title) like upper('%'||#{keyword}||'%')
and d.docu_code = #{docuCode}
d.status = '진행중' and approval_order = 3 and d.document_seq in (select document_seq from approvals_line where emp_no = #{empNo} and status = #{status})

select * from ( select d.document_seq document_seq, write_date, name, emer_yn, title, d.emp_no emp_no, a.emp_no last_appr, row_number() over (order by write_date desc, emer_yn desc) as rn from documents d join document_list dl on d.docu_code = dl.docu_code join approvals_line a on d.document_seq = a.document_seq where and upper(title) like upper('%'||#{keyword}||'%')
and d.docu_code = #{docuCode}
d.status = '진행중' and approval_order = 3 and d.document_seq in (select document_seq from approvals_line where emp_no = #{empNo} and status = #{status}) ) where rn between (#{cPage} * #{recordCountPerPage} - (#{recordCountPerPage} - 1)) AND (#{cPage} * #{recordCountPerPage})

select * from ( select d.document_seq document_seq, write_date, name, emer_yn, title, d.status status, a.emp_no last_appr, done_date, row_number() over (order by write_date desc) as rn from documents d join document_list dl on d.docu_code = dl.docu_code join approvals_line a on d.document_seq = a.document_seq where a.approval_order = 3 and d.status != '임시 저장' and d.emp_no = #{empNo} and upper(title) like upper('%'||#{keyword}||'%')
and d.docu_code = #{docuCode}
) where rn between (#{cPage} * #{recordCountPerPage} - (#{recordCountPerPage} - 1)) AND (#{cPage} * #{recordCountPerPage})

